mapper.insert:
INSERT INTO ausertb(
			  uidx
			, userID
			, userPassword
			, userName
			, userEmail
			, userPhone
			, userGender
			, userBirth
			) VALUES(
			  auidx_seq.nextval
			, #{userID}
			, #{userPassword}
			, #{userName}
			, #{userEmail}
			, #{userPhone}
			, #{userGender}
			, #{userBirth}
			)

mapper.select:

SELECT COUNT(userID) 
	FROM ausertb 
	WHERE userID = #{userID}

mapper.delete:
DELETE FROM ausertb
	WHERE userID = #{userID}
	AND userPassword = #{userPassword}
